import React from "react";
import { Link, useLoaderData, useSearchParams } from "react-router-dom";
import { getVans } from "../api";

export function loader() {
  return getVans();
}

export default function Vans() {
  const vans = useLoaderData();

  const [vanFilter, setVanFilter] = useSearchParams();
  const typeFilter = vanFilter.get("type");
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  const vanArray = typeFilter ? vans.filter((van) => van.type.toLowerCase() === typeFilter) : vans;

  const vansData = vanArray.map((van) => {
    return (
      <Link
        state={{ search: `?${vanFilter.toString()}`, type: typeFilter }}
        to={van.id}
        id="vansdatadivlink"
        key={van.id} // Add a unique key for each van in the map
      >
        <div id="vansdatadiv">
          <img src={van.imageUrl} alt="" id="vansimage" />
          <div>
            <div id="vandata-nameprice">
              <h2>{van.name}</h2>
              <p>${van.price}<span>/day</span></p>
            </div>
          </div>
          <div id={van.type === "Luxury" ? "luxurydiv" : van.type === "Simple" ? "simplediv" : "ruggeddiv"}>
            {van.type}
          </div>
        </div>
      </Link>
    );
  });

  React.useEffect(() => {
    setLoading(true);
    getVans()
      .then((data) => {
        setLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setError(err);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <h1 id="loadh1">Loading...</h1>;
  }

  if (error) {
    return <h1 id="loadh1">There was an error: {error.message}</h1>;
  }

  return (
    <>
      <div id="vanspage">
        <h1 id="vanspagehead">Explore our van options</h1>

        <div id="vanscategorydiv">
          <button
            onClick={() => setVanFilter({ type: "simple" })}
            className="vanscategorybtn"
            id="vanscategorybtn-simple"
            style={typeFilter === "simple" ? { backgroundColor: "#E17654", color: "white" } : null}
          >
            Simple
          </button>
          <button
            onClick={() => setVanFilter({ type: "luxury" })}
            className="vanscategorybtn"
            id="vanscategorybtn-luxury"
            style={typeFilter === "luxury" ? { backgroundColor: "#161616", color: "white" } : null}
          >
            Luxury
          </button>
          <button
            onClick={() => setVanFilter({ type: "rugged" })}
            className="vanscategorybtn"
            id="vanscategorybtn-rugged"
            style={typeFilter === "rugged" ? { backgroundColor: "#115E59", color: "white" } : null}
          >
            Rugged
          </button>

          {typeFilter && (
            <button onClick={() => setVanFilter()} className="vanscategorybtn-clear" id="clearfilterbtn">
              Clear filters <i className="fa-solid fa-trash"></i>
            </button>
          )}
        </div>

        <div id="vanslist">{vansData}</div>
      </div>
    </>
  );
}
