import React from "react";
import { useParams } from "react-router";
import { Link} from "react-router-dom";



export default function Hostvandetail(){
    const params = useParams()
    const [van, setVan] = React.useState(null)

    React.useEffect( () => {
         fetch(`/api/vans/${params.id}`)
         .then(res => res.json())
         .then(data => setVan(data.vans))
    },[params.id])


    return(
    <>



            <p className="host-back-to-van-link"><i class="fa-solid fa-arrow-left" id="host-back-to-van-link"></i> <Link id="host-back-to-van-link2"  to="/host/vans">Back to all vans</Link> </p>


{ van ?
           <div id="hostvandetaildiv">

            <div id="hostvandetailfirstrow">

           <img id="hostvandetailimage" alt="" src={van.imageUrl}/>

<div >
           <div id= {van.type=== "Luxury" ? "hostvandetailluxury" :  van.type === "Simple" ? "hostvandetailsimple" : "hostvandetailrugged"}>{van.type}</div>

           <h1>{van.name}</h1>
           <h2><b></b>${van.price}/day</h2>
           </div>

           </div>



           <p> <b>Name:</b> {van.name}</p>

<p><b> Category:</b> {van.type}</p>

<p><b>Description: </b>{van.description}</p>

<p><b>Visibility:</b> Public</p>







           </div>

           : <h2> Loading...</h2>
}






        </>
    )
}
