import React from "react";
import { useParams } from "react-router";
import { Link, NavLink } from "react-router-dom";



export default function Hostvandetail(){
    const params = useParams()
    const [van, setVan] = React.useState(null)

    React.useEffect( () => {
         fetch(`/api/vans/${params.id}`)
         .then(res => res.json())
         .then(data => setVan(data.vans))
    },[params.id])


    return(
    <>
 <div id="hostvandetailpage">
            <p className="back-to-van-link"><i class="fa-solid fa-arrow-left"></i> <Link id="back-to-van-link" to="/host/vans">Back to all vans</Link> </p>


{ van ?
           <div id="hostvandetaildiv">
           <img id="hostvandetailimage" alt="" src={van.imageUrl}/>
           <div id= {van.type=== "Luxury" ? "hostvandetailluxury" :  van.type === "Simple" ? "hostvandetailsimple" : "hostvandetailrugged"}>{van.type}</div>
           <h1>{van.name}</h1>
           <h2><b></b>${van.price}/day</h2>
           <p>{van.description}</p>

           <div id="link-right">
       <NavLink to={`/host/vans/${van.id}`} className = {({isActive}) => isActive  ? "routelink-active" : "routelinks"} >Host</NavLink>
        <NavLink to={`/host/vans/${van.id}/pricing`} className = {({isActive}) => isActive  ? "routelink-active" : "routelinks"} >About</NavLink>
        <NavLink to={`/host/vans/${van.id}/photos`} className = {({isActive}) => isActive  ? "routelink-active" : "routelinks"}>Vans</NavLink>
        </div>

        <p>Name: {van.name}</p>

        <p> Category: {van.type}</p>

        <p>Description: {van.description}</p>

        <p>Visibility: Public</p>



           </div>

           : <h2> Loading...</h2>
}





            </div>
        </>
    )
}
