import React from "react"
import { NavLink, Outlet } from "react-router-dom"



export default function HostVanDetaillayout() {

        const params = useParams()
        const [van, setVan] = React.useState(null)

        React.useEffect( () => {
             fetch(`/api/vans/${params.id}`)
             .then(res => res.json())
             .then(data => setVan(data.vans))
        },[params.id])

    return (
        <>
       <div id="hostvandetails-link-right">
       <NavLink to={`/host/vans/${van.id}`} className = {({isActive}) => isActive  ? "hostvandetailroutelink-active" : "hostvandetailroutelinks"} >Details</NavLink>
        <NavLink to={`/host/vans/${van.id}/pricing`} className = {({isActive}) => isActive  ? "hostvandetailroutelink-active" : "hostvandetailroutelinks"} >Pricing</NavLink>
        <NavLink to={`/host/vans/${van.id}/photos`} className = {({isActive}) => isActive  ? "hostvandetailroutelink-active" : "hostvandetailroutelinks"}>Photos</NavLink>
        </div>
        <Outlet/>
</>
        )
        }
