import React from "react"
import {  Link, NavLink, Outlet, useParams } from "react-router-dom"



export default function HostVanDetaillayout() {

        const params = useParams()
        const [van, setVan] = React.useState(null)

        React.useEffect( () => {
             fetch(`/api/vans/${params.id}`)
             .then(res => res.json())
             .then(data => setVan(data.vans))
        },[params.id])

    return (
        <>


        <div id="hostvandetailpage">
            <p className="host-back-to-van-link"><i class="fa-solid fa-arrow-left" id="host-back-to-van-link"></i> <Link id="host-back-to-van-link2"  to="/host/vans">Back to all vans</Link> </p>


            {van ? (
  <div id="hostvandetaildiv">
    <div id="hostvandetailfirstrow">
      <img id="hostvandetailimage" alt="" src={van.imageUrl} />
      <div>
        <div
          id={
            van.type === "Luxury"
              ? "hostvandetailluxury"
              : van.type === "Simple"
              ? "hostvandetailsimple"
              : "hostvandetailrugged"
          }
        >
          {van.type}
        </div>
        {van.name && <h1>{van.name}</h1>}
        {van.price && (
          <h2>
            <b></b>${van.price}/day
          </h2>
        )}
      </div>
    </div>
    <div id="hostvandetails-link-right">
      <NavLink
        to={`/host/vans/${van.id}`}
        className={({ isActive }) =>
          isActive ? "hostvandetailroutelink-active" : "hostvandetailroutelinks"
        }
      >
        Details
      </NavLink>
      <NavLink
        to={`/host/vans/${van.id}/pricing`}
        className={({ isActive }) =>
          isActive ? "hostvandetailroutelink-active" : "hostvandetailroutelinks"
        }
      >
        Pricing
      </NavLink>
      <NavLink
        to={`/host/vans/${van.id}/photos`}
        className={({ isActive }) =>
          isActive ? "hostvandetailroutelink-active" : "hostvandetailroutelinks"
        }
      >
        Photos
      </NavLink>
    </div>
    <Outlet />
  </div>
) : (
  <h2>Loading...</h2>
)}

</div>

</>
        )
}
