import React from "react";
import { Link, useParams, useLocation } from "react-router-dom";




export default function VanDetail(){

    const params = useParams()
    const [van, setVan] = React.useState(null)
    const location = useLocation()

    const vanType = (location.state.type).charAt(0).toUpperCase() + (location.state.type).slice(1)
var type

if(location.state.type === "simple" || location.state.type === "luxury" || location.state.type === "rugged") {
 type = `${vanType}`
}
  else if (location.state.type === null){
    type = "all"
  }

const search = location.state?.search || ""




    React.useEffect( () => {
         fetch(`/api/vans/${params.id}`)
         .then(res => res.json())
         .then(data => setVan(data.vans))
    },[params.id])



    return(
        <>
        <div id="vandetailpage">
            <p className="back-to-van-link"> <Link id="back-to-van-link" to= {`../${search}`} relative="path"><i class="fa-solid fa-arrow-left"></i>Back to {type} vans</Link> </p>


{ van ?
           <div id="vandetaildiv">
           <img id="vandetailimage" alt="" src={van.imageUrl}/>
           <div id= {van.type=== "Luxury" ? "vandetailluxury" :  van.type === "Simple" ? "vandetailsimple" : "vandetailrugged"}>{van.type}</div>
           <h1>{van.name}</h1>
           <h2><b></b>${van.price}/day</h2>
           <p>{van.description}</p>
           <div id="vandetailbuttondiv">
           <button type="submit" id="vandetailbutton">Rent this van </button></div>
           </div> 
           : <h2> Loading...</h2>
}





            </div>
        </>
    )
}
