import React from "react";
import "../server"




export default function Vans(){

    const[vans ,setVans] = React.useState([])


    React.useEffect(function() {
        fetch("/api/vans")
            .then(res => res.json())
            .then(data => setVans(data.vans))
    }, [])



    function vanFilter(){
setVans(van => vans.filter(checkVantype()))
    }

    function checkVantype(event){
var vanType = event.target.value
return vanType

    }




    const vansData = vans.map(van => {
        return (
            <div id="vansdatadiv">
                <img src={van.imageUrl} alt="" id="vansimage"/>
            <div>

            <div id="vandata-nameprice">
            <h2>{van.name}</h2>
            <p>${van.price}<span>/day</span></p>
            </div>

            </div>
            <div id = {van.type=== "Luxury" ? "luxurydiv" :  van.type === "Simple" ? "simplediv" : "ruggeddiv"} >
                {van.type}
                </div>
        </div>


        )
    })


    return(
        <>
        <div id="vanspage">
        <h1 id="vanspagehead">Explore our van options</h1>

        <div id="vanscategorydiv">
            <button className="vanscategorybtn">Simple </button>
            <button className="vanscategorybtn" onClick={checkVantype() &&  vanFilter()}>Luxury</button>
            <button className="vanscategorybtn">Rugged </button>
            <button  className="vanscategorybtn" id="clearfilterbtn">Clear filters </button>
        </div>

<div id="vanslist">
{vansData}
</div>


<div id="aboutpagefooter"> 
         â’¸ 2022 #VANLIFE
          </div>




        </div>
        </>
    )
}
